SELECT
sum(stock_num) AS totalNum
FROM stock_quant


        AND brand_id = #{brandId,jdbcType=VARCHAR}


        AND store_id = #{storeId,jdbcType=VARCHAR}


        AND goods_id = #{goodsId,jdbcType=VARCHAR}


        AND (goods_bar_code Like concat(#{condition,jdbcType=VARCHAR}, '%')
        OR goods_code Like concat(#{condition,jdbcType=VARCHAR}, '%')
        OR goods_name Like concat(#{condition,jdbcType=VARCHAR}, '%')
        )


        AND features LIKE concat('%', #{features,jdbcType=VARCHAR} , '%')

    AND valid_status = 1

    GROUP BY goods_id

    HAVING


    totalNum >= #{minAmount,jdbcType=BIGINT}


    AND


    totalNum <= #{maxAmount,jdbcType=BIGINT}

