SELECT
id, stock_quant_id stockQuantId, brand_id, store_id, warehouse_id warehouseId, goods_id goodsId, goods_name goodsName, classify_id classifyId, classify_name classifyName,
goods_type, unit_id, unit_name unitName, unit_price unitPrice, stock_num stockNum, stock_upper, stock_lower, stock_safe, create_time,
initial_unit_price initialUnitPrice, initial_stock_num initialStockNum,
update_time, goods_bar_code goodsBarCode, stock_num_usable, stock_num_using, stock_num_on_order, goods_code goodsCode, goods_type_name, features features,goods_brand_name
FROM stock_quant


        AND brand_id = #{brandId,jdbcType=VARCHAR}


        AND store_id = #{storeId,jdbcType=VARCHAR}


        AND goods_id = #{goodsId,jdbcType=VARCHAR}


        AND warehouse_id = #{warehouseId,jdbcType=VARCHAR}


        AND (goods_bar_code Like concat(#{condition,jdbcType=VARCHAR}, '%')
        OR goods_code Like concat(#{condition,jdbcType=VARCHAR}, '%')
        OR goods_name Like concat(#{condition,jdbcType=VARCHAR}, '%')
        )


        AND features LIKE concat('%', #{features,jdbcType=VARCHAR} , '%')


        AND stock_num >= #{minAmount,jdbcType=BIGINT}


        AND stock_num <= #{maxAmount,jdbcType=BIGINT}

    AND valid_status = 1

ORDER BY stock_num ASC

    LIMIT #{recordNo,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT id, stock_quant_id stockQuantId, brand_id, store_id, warehouse_id warehouseId, goods_id goodsId, goods_name goodsName, classify_id classifyId, classify_name classifyName, goods_type, unit_id, unit_name unitName, sum(unit_price * stock_num) totalCash, sum(stock_num) AS totalNum, sum(unit_price * stock_num)/sum(stock_num) totalUnitPrice, stock_upper, stock_lower, stock_safe, create_time, initial_stock_num initialStockNum, initial_unit_price initialUnitPrice, sum(initial_stock_num * initial_unit_price) initialTotalCash, sum(initial_stock_num) initinalTotalNum, sum(initial_stock_num * initial_unit_price)/sum(initial_stock_num) initinalTotalPrice, update_time, goods_bar_code goodsBarCode, stock_num_usable, stock_num_using, stock_num_on_order, goods_code goodsCode, goods_type_name, features features,goods_brand_name FROM stock_quant
AND brand_id = #{brandId,jdbcType=VARCHAR}
AND store_id = #{storeId,jdbcType=VARCHAR}
AND goods_id = #{goodsId,jdbcType=VARCHAR}
AND (goods_bar_code Like concat(#{condition,jdbcType=VARCHAR}, '%') OR goods_code Like concat(#{condition,jdbcType=VARCHAR}, '%') OR goods_name Like concat(#{condition,jdbcType=VARCHAR}, '%') )
AND features LIKE concat('%', #{features,jdbcType=VARCHAR} , '%')
AND valid_status = 1 GROUP BY goods_id
HAVING
totalNum >= #{minAmount,jdbcType=BIGINT}
AND
totalNum <= #{maxAmount,jdbcType=BIGINT}
ORDER BY totalNum ASC
LIMIT #{recordNo,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

